function alpha = fonc_a(xm,ym)%----Epaisseur des matériaux----  ebet = 0.3;  ever = 0.08;%----Conductivité thermique des matériaux----  Kbet = 0.92;  Kver = 1.2;%----Facteur de transfert des matériaux-----  abet = Kbet/ebet;  aver = Kver/ever;%----fonction----  a = @(x,y) all(aver.*(((x>=1.0).*(x<=2.5).*(y==0.0))...				          +((x>=1.0).*(x<=2.5).*(y==7.53)))...		        +abet*(((x>=0.0).*(x<1.0).*(y==0.0))+((x>2.5).*(x<=5.2).*(y==0.0))...				          +((x>=0.0).*(x<1.0).*(y==7.53))+((x>2.5).*(x<=5.9).*(y==7.53))));  alpha = a(xm,ym);  end