function f = fonc_f(xm,ym)  f1 = 9;  f2 = 9;  f3 = 1.5;  f4 = 3;  fs = @(x,y) all(f1*((x>=1.0)*(x<=2.5)*(y>=0.05)*(y<=0.15))...		        +f2*((x>=1.0)*(x<=2.5)*(y>=7.38)*(y<=7.48))...		        +f3*((x>=6.2)*(x<=6.9)*(y>=2.81)*(y<=2.91))...		        +f4*((x>=1.86)*(x<=2.56)*(y>=2.81)*(y<=2.93)));  f = fs(xm,ym);end