function kappa = fonc_k(xm,ym)  Kair = 0.0262;  Kver = 1.2;  Kboi = 0.15;  Kbet = 0.92;  Kplac = 0.04;  Kalu = 237;  k =@(x,y) all(Kalu*(((x>=1.0)*(x<=2.5)*(y>=0.05)*(y<=0.15))...				 +((x>=1.0)*(x<=2.5)*(y>=7.38)*(y<=7.48))...				 +((x>=6.2)*(x<=6.9)*(y>=2.81)*(y<=2.91))...				 +((x>=1.86)*(x<=2.56)*(y>=2.81)*(y<=2.91)))...		+ Kboi*((x>=7.2)*(x<=8.1)*(y==2.76))...		+ Kver*(((x>=1.0)*(x<=2.5)*(y==0.0))...				 +((x>=1.0)*(x<=2.5)*(y==7.53)))...		+ Kbet*(((y>=4.23)*(y<=7.53)*(x==0.0))...				 +((y>=2.76)*(y<=4.03)*(x==0.0))...				 +((y>=0.0)*(y<=2.56)*(x==0.0))...			 	 +((y>=4.23)*(y<=7.53)*(x==5.9))...				 +((y>=0.0)*(y<=2.56)*(x==5.2))...				 +((x>=3.81)*(x<7.2)*(y==2.76))...				 +((x>=0.0)*(x<1.0)*(y==0.0))+((x>2.5)*(x<=5.2)*(y==0.0))...				 +((x>=0.0)*(x<1.0)*(y==7.53))+((x>2.5)*(x<=5.9)*(y==7.53)))...    + Kplac*(((x>=3.81)*(x<5.2)*(y==2.56))...			   +((x>0.0)*(x<=2.86)*(y==2.56))...			   +((x>0.0)*(x<=2.86)*(y==2.76))...			   +((x>0.0)*(x<=2.66)*(y==4.03))...			   +((x>=2.66)*(x<=2.86)*(y==3.76))...			   +((x>0.0)*(x<=2.86)*(y==4.23))...			 	 +((x>=3.81)*(x<5.9)*(y==4.23))...			 	 +((x>=3.81)*(x<8.1)*(y==4.03))...		  	 +((y>=2.56)*(y<=2.76)*(x==2.86))...			 	 +((y>=3.71)*(y<=4.03)*(x==2.66))...			 	 +((y>=3.71)*(y<=4.23)*(x==2.86))...			 	 +((y>=4.03)*(y<=4.23)*(x==3.81))...			 	 +((y>=2.56)*(y<=4.23)*(x==3.81)))...		+ Kair*(((x>0.0)*(x<5.2)*(y>0.0)*(y<2.56))...				 +((x>0.0)*(x<5.9)*(y>4.23)*(y<7.53))...				 +((x>3.81)*(x<8.1)*(y>2.76)*(y<4.03))...				 +((x>2.86)*(x<3.81)*(y>0.0)*(y<7.53))...				 +((x>0.0)*(x<2.66)*(y>2.76)*(y<4.03))...				 +((x>=2.66)*(x<=2.86)*(y>2.76)*(y<3.71))...				 -((x>=1.0)*(x<=2.5)*(y>=0.05)*(y<=0.15))...				 -((x>=1.0)*(x<=2.5)*(y>=7.38)*(y<=7.48))...				 -((x>=6.1)*(x<=6.9)*(y>=2.81)*(y<=2.91))...				 -((x>=1.86)*(x<=2.56)*(y>=2.81)*(y<=2.91))));kappa = k(xm,ym);         end