function uE = fonc_uE(xm,ym)  E = @(x,y) all((((x>=1.0).*(x<=2.5).*(y==0.0))...				          +((x>=1.0).*(x<=2.5).*(y==7.53)))...		        +(((x>=0.0).*(x<1.0).*(y==0.0))+((x>2.5).*(x<=5.2).*(y==0.0))...				          +((x>=0.0).*(x<1.0).*(y==7.53))+((x>2.5).*(x<=5.9).*(y==7.53))));  uE = 5.0*E(xm,ym);         end