function x = gradient_conjugue_preconditione(A,b)%Transforamtion du système linéaire Ax=b <=> (At)(b)  %K = 1./sqrt(diag(A)); % Ici c'est K^(-1/2)  %bt = K * b;  %At = K[1:n-1] * diag(A,1) * K[2:n];  K = diag(1./sqrt(diag(A))); % Ici c'est K^(-1/2)  bt = K * b;  At = K * A * K;  % si on a un polynome Pn ou n>1 on fait une boucle ou le premier K va de 1 à n-i et l'autre de i+1 à n%Méthode du gradient conjugué  n = length(A);  x0 = zeros(n,1);  seuil = 1e-5;  kmax = 1000;  xt = mon_gc(At,bt,x0,seuil,kmax);%Solution du système Ax=b  x = K * xt;end