function [Ma]=lit_fichier_msh(fichier_msh)%---------Ouverture du fichier------------  fichier = fopen(fichier_msh,'r'); %---------Lecture du fichier------------   nbn = fscanf(fichier,'%d',1);  nbe = fscanf(fichier,'%d',1);  nba = fscanf(fichier,'%d',1);%---------Allocation des matrices---------   coord = zeros(nbn,2);  refn = zeros(nbn,1);  tri = zeros(nbe,3);  reft = zeros(nbe,1);  ar = zeros(nba,2);  refa = zeros(nba,1);  for i = 1 : nbn    coord(i,:) = fscanf(fichier,'%f',2);    refn(i) = fscanf(fichier,'%d',1);  end  for i = 1 : nbe    tri(i,:) = fscanf(fichier,'%d',3);    reft(i) = fscanf(fichier,'%d',1);  end  for i = 1 : nba    ar(i,:) = fscanf(fichier,'%d',2);    refa(i) = fscanf(fichier,'%d',1);  end  fclose(fichier);  Ma = struct('nbn',nbn,'nbe',nbe,'nba',nba,'coord',coord,'tri',tri,'ar',ar,'refn',refn,'reft',reft,'refa',refa);end