function [h,Qh] = pas_et_qualite_maillage(S)  L = [0 0 0];  h = -1;  Qh = -1;  a = sqrt(3)/6;  for i = 1 : S.nbe%------------Recuperation du triangle---------------    A = S.coord(S.tri(i,1),:);    B = S.coord(S.tri(i,2),:);    C = S.coord(S.tri(i,3),:);%-------Calcul coordonees des vecteurs du triangle--------    x1 = C(1) - A(1);    x2 = C(1) - B(1);    x3 = B(1) - A(1);    y1 = C(2) - A(2);    y2 = C(2) - B(2);    y3 = B(2) - A(2);%-------Longueur des cotes du triangle--------    L(1) = sqrt( x1^2 + y1^2 );    L(2) = sqrt( x2^2 + y2^2 );    L(3) = sqrt( x3^2 + y3^2 );%-----------Calcul pas et qualite------------    mesT = 1/2 * abs((x1*y2)-(y1*x2));    rhoT = mesT / (0.5*sum(L));    hT = max(L);    QT =  a * (hT/rhoT);    if hT > h      h = hT;    end    if QT > Qh      Qh = QT;    end  endend